import React, { useState , useEffect } from 'react';
import {List , Row , Col , Modal , message , Button ,Icon } from 'antd'
import axios from 'axios'
import servicePath from '../config/apiUrl'
const { confirm } = module

function ArticleList (props){
    const [ Articlelist , setArticleList ] = useState([])

    useEffect(()=>{
        getArticleList()
    },[])

    const getArticleList = ()=>{
        axios({
            method:'get',
            url:servicePath.articleList
        }).then(
            res=>{
                console.log(res.data.date[0])
                console.log(typeof(res.data.date))
                setArticleList(res.data.date)
            }
        )
    }

    return (
        <div>
            <List 
              // 头部
              header={
                <Row className="list-div">
                    <Col span={8}>
                        <b>标题</b>
                    </Col>
                    <Col span={3}>
                        <b>类别</b>
                    </Col>
                    <Col span={3}>
                        <b>发布时间</b>
                    </Col>
                    <Col span={3}>
                        <b>集数</b>
                    </Col>
                    <Col span={3}>
                        <b>浏览量</b>
                    </Col>

                    <Col span={4}>
                        <b>操作</b>
                    </Col>
                </Row>
                }
              bordered
              //数据源
              dataSource={Articlelist}
              //每一项的设置
              renderItem={item=>(
                <List.Item>
                  {/* 标题 */}
                  <div className="list-title">

                  </div>
                  {/* 图标 */}
                  <div className="list-icon">
                    {/* 日期 */}
                    <span><Icon type="calendar" /> 2019-06-28</span>
                    <span><Icon type="folder" /> 视频教程</span>
                    <span><Icon type="fire" /> 6000</span>
                  </div>
                  {/* 内容 */}
                  <div className="list-context">
                    {item.introduce}
                  </div>
                </List.Item>
              )}
            />
        </div>
    )
} 

export default ArticleList
